css:
.print-format {
    padding: 0.30in !important;
    font-size: 15px !important;
    max-width: 100%;
}

.print-format .letter-head {
    margin-bottom: 0px;
}

.print-format th {
    color: #000;
    font-size: 15px;
    font-weight: bold;
    padding: 7px !important;
}

.print-format td {
    color: #000;
    font-size: 14px;
    font-weight: normal;
    padding: 7px !important;
}

.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid #000;
}

.table > caption + thead > tr:first-child > th, 
.table > colgroup + thead > tr:first-child > th, 
.table > thead:first-child > tr:first-child > th, 
.table > caption + thead > tr:first-child > td, 
.table > colgroup + thead > tr:first-child > td, 
.table > thead:first-child > tr:first-child > td {
    border-top: 1px solid #000 !important;
}

.table {
    margin: 0px;
}

.bordered-table {
    border-collapse: collapse;
    width: 100%;
}

.bordered-table th, .bordered-table td {
    border: 1px solid #000; /* Adjust border color as needed */
    padding: 8px; /* Optional: Add padding for better readability */
    text-align: left; /* Adjust text alignment */
}


.iteminfo{
    font-size: 13px;
    padding-top: 8px !important;
    line-height: 1.4;
}
h1 {
    border-bottom: 2px solid #bbc4c2;
}

.docname {
    color: #7e7c73;
    font-size: 15px;
    color: #a89392;
}

.three-part-container {
    display: flex;
    justify-content: space-between;
    margin: 20px 0px;
}

.part {
    width: 30%;      
}

th {
    font-weight: bold;
    color: #000;
}

.textleft {
    text-align: left !important;
}

.textright {
    text-align: right !important;
}

.totalmainpart {
    margin-top: 20px;
}

.totalpart {
    display: flex;
    font-size: 15px;
    padding: 4px 0;
}

.totalpart1 {
    flex: 46%;
}

.totalpart2 {
    flex: 27%;
}

.termsotherinfo {
    margin: 20px 0px;
}
@media (min-width: 200px) and (max-width: 500px)  {
    .three-part-container {
        flex-direction: column;
        margin: 10px 0;
    }
    .three-part-container {
    display: flex;
    justify-content: space-between;
    margin: 20px 0px;
}

    .part {
        width: 100%;
        margin-bottom: 10px;
    }
    .bordered-table th, .bordered-table td {
        font-size: 12px;
        padding: 5px;
    }

    .totalpart {
        display: flex;
        font-size: 14px;
    }
    .totalpart1 {
        flex: 0%;
    }

    .totalpart2 {
        flex: 100%;
    }
    .three-part-container {
    display: flex;
    justify-content: space-between;
    margin: 20px 0px;
}

    .textleft, .textright {
        text-align: left !important;
    }
    h1 {
        font-size: 18px;
    }
}

